def A(k: int, x):
    return sum(map(lambda xi: xi**k, x)) / len(x)


def B(k: int, x):
    if k == 1:
        return 0
    A1 = A(1, x)
    return sum(map(lambda xi: (xi-A1)**k, x)) / len(x)


x = list(map(float, input().split()))

B2 = B(2, x)
print('{:.4f} {:.4f}'.format(B(3, x)/(B2**1.5), B(4, x)/(B2**2)))
